import React,{useState} from 'react';
const ItemCount = () => {
    const [count, setCount] = useState(0);

return (
        <div>
            <h2>(count)</h2>
            <button onClick={()=> setCount(count + 1)}>Add to Cart</button>
            <button onClick={()=> setCount(count - 1)}>Remove from Cart</button>
        </div>
    );
};

export default ItemCount;
