import React,{useEffect, useState} from 'react';

export const ItemCount = ({currentValue, stock, onAdd}) => {
    const [count, setCount]  = useState(parseInt(currentValue));

    const decrease = () => {
        setCount(count - 1);
    }

    const increase = () => {
        setCount(count + 1);
    }

    useEffect(() => {
        setCount(parseInt(currentValue));
    }, [currentValue])

return (
        <div className="buttom">
            <button  className="btn" onClick={increase}>+</button>
            <p>{count}</p>
            <button className="btn" onClick={decrease}>-</button>
        </div>
    );
};

export default ItemCount;
